import { WalletConnectBase } from "./wallet-connect/WalletConnectBase";
import { TRUST_ICON } from "../../assets/svgs";
export class TrustWallet extends WalletConnectBase {
    getMeta() {
        return TrustWallet.meta;
    }
}
TrustWallet.id = "trust";
TrustWallet.meta = {
    name: "Trust Wallet",
    iconURL: TRUST_ICON,
    links: {
        native: "trust:",
        universal: "https://link.trustwallet.com",
    },
};
export const trustWallet = (config) => {
    return {
        id: TrustWallet.id,
        meta: TrustWallet.meta,
        create: (options) => new TrustWallet({
            ...options,
            walletId: "trust",
            projectId: config?.projectId,
        }),
        recommended: config?.recommended,
    };
};
